pub struct Solution {}

impl Solution {
    pub fn repeated_string_match(a: String, b: String) -> i32 {
        //println!("a: {}, b: {}", a, b);
        let mut aa = a.to_owned();
        let mut bb = b.to_owned();
        let mut found = false;

        let min_len = b.len();
        let max_len = b.len() + 2 * a.len();
        //println!("min_len: {}, max_len: {}", min_len, max_len);

        // for _ in 0..min_count - 1 {
        //     aa.push_str(&a);
        // }

        while aa.len() < min_len {
            aa.push_str(&a);
        }

        // while count <= len_b / len_a + 2 {
        while aa.len() <= max_len {
            // println!("count: {}", count);
            match Solution::is_substring(&aa, &bb) {
                true => {
                    //println!("aa: {:?}", aa);
                    found = true;
                    break;
                }
                false => {
                    aa.push_str(&a);
                }
            }
            // count += 1;
        }

        match found {
            true => {
                //println!("count: {}", count);
                aa.len() as i32 / a.len() as i32
            }
            false => {
                //println!("not found");
                -1
            }
        }
    }

    pub fn is_substring(a: &str, b: &str) -> bool {
        let a = a.as_bytes();
        let b = b.as_bytes();
        let mut i = 0;
        let mut j = 0;

        while i < a.len() && j < b.len() {
            if a[i] == b[j] {
                j += 1;
            } else {
                i -= j;
                j = 0;
            }
            i += 1;
        }
        j == b.len()
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_686() {
        assert_eq!(
            Solution::repeated_string_match("abc".to_string(), "cabcabca".to_string()),
            4
        );
        assert_eq!(
            Solution::repeated_string_match("a".to_string(), "a".to_string()),
            1
        );
        assert_eq!(
            Solution::repeated_string_match("abcd".to_string(), "cdabcdab".to_string()),
            3
        );
        assert_eq!(
            Solution::repeated_string_match("a".to_string(), "aa".to_string()),
            2
        );
        assert_eq!(
            Solution::repeated_string_match("a".to_string(), "".to_string()),
            -1
        );
    }
}
